var celia1info=document.getElementById("celia1-info"),celia1Recom=document.getElementById("celia1-recomandation"),celia2info=document.getElementById("celia2-info"),celia2Recom=document.getElementById("celia2-recomandation"),celia3info=document.getElementById("celia3-info"),celia3Recom=document.getElementById("celia3-recomandation");function openInfo1(){celia1info.classList.toggle("visible")}function openRecom1(){celia1Recom.classList.toggle("visible")}function openInfo2(){celia2info.classList.toggle("visible")}function openRecom2(){celia2Recom.classList.toggle("visible")}function openInfo3(){celia3info.classList.toggle("visible")}function openRecom3(){celia3Recom.classList.toggle("visible")}document.addEventListener("DOMContentLoaded",e=>{window.addEventListener("resize",detectOrientation),detectOrientation()});var animation=document.getElementById("rotateScreen"),content=document.getElementById("displayContent");function detectOrientation(){0==window.orientation?animation.style.display="block":(animation.style.display="none",content.style.display="block")}